int N;
vector<int> X;

vector<vector<int> > cc;
int dp(int l, int r) {
    int &ret = cc[l][r];
    if(ret != -1) return ret;

    ret = 3*(r - l + 1);
    int pre = 1;
    int tmp = 0;
    for(int i = l; i <= r; i++) {
        tmp += (X[i] - pre) % 2 == 0? (X[i] - pre == 0? 0 : 2) : (pchk[ X[i] - pre ]? 3 : 1);
        pre = X[i] + 1;
    }
    if(pchk[ X[r] ]) tmp += (X[r] % 2 == 0? 2 : 3);
    else tmp++;

    ret = min(ret, tmp);
    for(int i = l; i < r; i++) {
        ret = min(ret, dp(l, i) + dp(i + 1, r));
    }
    return ret;
}

