	comp[0]=1;
	comp[1]=1;
	for (i=2;i<=N;i++)
	{
		if (!comp[i]) prime[++primes]=i;
		for (j=1;j<=primes&&i*prime[j]<=N;j++)
		{
			comp[i*prime[j]]=1;
			if (i%prime[j]==0) break;
		}
	}
	comp[2]=1;
}

bool match(int u)
{
	for (int i=1;i<=m;i++)
	{
		if (comp[abs(u-c[i])]||vis[i]) continue;
		vis[i]=1;
		if (!left[i]||match(left[i]))
		{
			left[i]=u;
			return 1;
		}
	}
	return 0;
}

