#include <cstdio>

const int N = 203, M = 10001003;
bool ispri[M], vs[N];
int a[N], tot;
int n, Ans;

void input() {
	int x, y;
	scanf("%d%d", &n, &x);
	a[tot = 1] = x;
	for(int i=2;i<=n;++i) {
		scanf("%d", &y);
		if(x+1 != y) a[++tot] = x+1, a[++tot] = y;
		x = y;
	}
	a[++tot] = ++x;

	for(int i=2;i<=x;++i) ispri[i] = true;
	for(int i=2;i<=x;++i) if(ispri[i])
		for(int j=i+i;j<=x;j+=i) ispri[j] = false;
	ispri[2] = false;
}

namespace Part1 {
	struct Edge {int v, w, nxt;} e[N*N];
	int head[N], cur[N];
	int q[N], he, ta, dis[N];
	int s, t, _;

	inline void add(int x, int y) {
		e[++_].v = y, e[_].w = 1, e[_].nxt = head[x], head[x] = _;
		e[++_].v = x, e[_].w = 0, e[_].nxt = head[y], head[y] = _;
	}

	void init() {
		s = tot + 1, t = s + 1, _ = 1;
		for(int i=1;i<=t;++i) head[i] = 0;
	}

	bool bfs() {
		for(int i=1;i<=t;++i) dis[i] = -1;
		dis[q[he = ta = 1] = s] = 0;
		while(he <= ta) {
			int x = q[he++];
			for(int i=head[x];i;i=e[i].nxt)
				if(e[i].w and -1 == dis[e[i].v]) {
					dis[q[++ta] = e[i].v] = dis[x] + 1;
					if(e[i].v == t) return true;
				}
		}
		return false;
	}

	int dfs(int x, int flw) {
		if(x == t or !flw) return flw;
		int res = 0, tmp;
		for(int&i=cur[x];i;i=e[i].nxt)
			if(e[i].w and dis[e[i].v] == dis[x] + 1)
			if(tmp = dfs(e[i].v, e[i].w)) {
				e[i].w -= tmp, e[i^1].w += tmp, res += tmp;
				if(res == flw) return res;
			}
		if(!res) dis[x] = -1;
		return res;
	}

	int Dinic() {
		const int oo = 1e9;
		int res = 0;
		while(bfs()) {
			for(int i=1;i<=t;++i) cur[i] = head[i];
			res += dfs(s, +oo);
		}
		return res;
	}

	inline int abs(const int x) {return x < 0 ? -x : x;}	

	void work() {
		init();
		for(int i=1;i<=tot;++i)
			if(a[i]&1) add(s, i);

		for(int i=1;i<=tot;++i) if(a[i]&1)
		for(int j=1;j<=tot;++j) if(~a[j]&1)
			if(ispri[abs(a[i] - a[j])]) add(i, j);

		for(int i=1;i<=tot;++i)
			if(~a[i]&1) add(i, t);

		Ans += Dinic();
		for(int i=head[s];i;i=e[i].nxt)
			if(!e[i].w) vs[e[i].v] = true;
		for(int i=head[t];i;i=e[i].nxt)
			if(e[i].w) vs[e[i].v] = true;
	}
}

void Part23() {
	int ct[2] = {0, 0};
	for(int i=1;i<=tot;++i)
		if(!vs[i]) ++ct[a[i]&1];
	Ans += ct[0] ^ (ct[0] & 1);
	Ans += ct[1] ^ (ct[1] & 1);
	Ans += (ct[0] & ct[1] & 1) * 3;
}

int main() {
	input();

	Part1::work();
	Part23();

	printf("%d\n", Ans);
	return 0;
}

