const int N=205,L=1e7+5;
bool mark[L],prime[L];
namespace match
{
  const int N=205;
  int l[N],r[N],n,m;
  bool a[N][N],seen[N];
  bool findmatch(int i)
  {
    rep(j,0,m)
      {
	if(a[i][j] && !seen[j])
	  {
	    seen[j]=true;
	    if(r[j]<0 || findmatch(r[j]))
	      {
		l[i]=j; r[j]=i;
		return true;
	      }
	  }
      }
    return false;
  }
  int bipartitematch()
  {
    int cnt=0;
    rep(i,0,n) l[i]=-1;
    rep(i,0,m) r[i]=-1;
    rep(i,0,n)
      {
	rep(j,0,m) seen[j]=false;
	if(findmatch(i)) cnt++;
      }
    return cnt;
  }
};
int main()
{
  std::ios::sync_with_stdio(false);
  cin.tie(NULL) ; cout.tie(NULL) ;
  memset(prime,true,sizeof(prime));
  prime[1]=false;
  rep(i,2,L)
    {
      if(!prime[i]) continue;
      for(int j=2*i;j<L;j+=i)
	prime[j]=false;
    }
  prime[2]=false;
  int n,x;
  cin>>n;
  rep(i,1,n+1)
    {
      cin>>x;
      mark[x]=true;
    }
  vi arrodd,arreven;
  int odd=0,even=0;
  rep(i,1,L-1)
    if(mark[i]!=mark[i-1])
      {
	if(i&1) arrodd.pb(i);
	else arreven.pb(i);
      }
  odd=sz(arrodd); even=sz(arreven);
  match::n=odd; match::m=even;
  rep(i,0,sz(arrodd))
    rep(j,0,sz(arreven))
    {
      if(prime[abs(arrodd[i]-arreven[j])]) match::a[i][j]=1;
      else match::a[i][j]=0;
    }
  int k=match::bipartitematch();
  assert((even-odd)%2==0);
  even-=k; odd-=k;
  int ans=k+(even/2+odd/2)*2+(odd&1)*3;
  cout<<ans<<endl;
  return 0 ;
}

#include <bits/stdc++.h>
using namespace std;

const int N = 210;

int n, x[N];
vector<int> dif;
int ans;
// for bipartite matching
vector<int> ver[2];
vector<int> g[N];
bool check[N];
int mx[N], my[N];

bool isprime(int x) {
	for (int i = 2; i * i <= x; ++i) if (x % i == 0) return false;
	return true;
}

bool dfs(int u) {
	if (!u) return true;
	for (int v : g[u]) if (!check[v]) {
		check[v] = 1;
		if (dfs(my[v])) {
			mx[u] = v; my[v] = u; return true;
		}
	}
	return false;
}

int main() {
	scanf("%d", &n);
	for (int i = 1; i <= n; ++i) scanf("%d", x + i);
	for (int i = 1; i <= n; ++i) {
		if (!(i > 1 && x[i - 1] == x[i] - 1)) {
			dif.push_back(x[i]);
		}
		if (x[i + 1] != x[i] + 1) {
			dif.push_back(x[i] + 1);
		}
	}

	for (int i : dif) ver[i % 2].push_back(i);

	for (int i = 0; i < ver[0].size(); ++i) {
		for (int j = 0; j < ver[1].size(); ++j) {
			int dist = abs(ver[0][i] - ver[1][j]);
			if (dist <= 2) continue;
			if (isprime(dist)) {
				g[i + 1].push_back(j + 1);
			}
		}
	}

	// bipartite matching
	int sz = ver[0].size();
	int rem = sz, prev_rem = sz + 1;

	while(rem > 0 && rem < prev_rem) {
		prev_rem = rem;
		for (int i = 1; i <= sz; ++i) check[i] = false;
		for (int i = 1; i <= sz; ++i) if (!mx[i]) rem -= dfs(i);
	}

	for (int i = 1; i <= sz; ++i) if (mx[i]) ++ans;

	int v0 = ver[0].size() - ans;
	int v1 = ver[1].size() - ans;

	ans += 2 * (v0 / 2); v0 %= 2;
	ans += 2 * (v1 / 2); v1 %= 2;

	ans += (v0 == 1 && v1 == 1) * 3;

	printf("%d\n", ans);
}

