            if(i<=10000) {for(int j=i*i;j<=MAX2;j+=i) p[j]=-1;}
        }
    }
    p[2]=-1;
    for(int i=0;i<cx.size();i++) c[cx[i]%2].push_back(cx[i]);
    for(int i=0;i<c[0].size();i++) for(int j=0;j<c[1].size();j++) if(p[abs(c[0][i]-c[1][j])]==1) {
        //printf("%d %d %d\n",i,j,abs(c[0][i]-c[1][j]));
        conn[i].push_back(make_pair(c[0].size()+j,1));
        conn[c[0].size()+j].push_back(make_pair(i,0));
    }
    for(int i=0;i<c[0].size();i++) {
        conn[c[0].size()+c[1].size()].push_back(make_pair(i,1));
        conn[i].push_back(make_pair(c[0].size()+c[1].size(),0));
    }
    for(int i=0;i<c[1].size();i++) {
        conn[i+c[1].size()].push_back(make_pair(c[0].size()+c[1].size()+1,1));
