public:
    PrimeTable(SDWORD lMax) {
        /* lSearchMax   *2 3 */
        if (!(1 <= lMax)) {
            printf("Oops!");
            return;
        }

        /*  */
        vbIsPrime.resize(lMax + 1, true);
        SDWORD lSearchMax = sqrt(lMax) + 1;
        vbIsPrime[0] = false;
