                for (SDWORD lCurNum = lPrime * 2; lCurNum <= lMax; lCurNum += lPrime) {
                    vbIsPrime[lCurNum] = false;
                }
            }
        }
    };

    bool IsPrime(SQWORD sqN)
    {
        return vbIsPrime[sqN];
    }
};


