#define PNT_NO_START    (0)
#define PNT_NO_OFFSET   (200)
#define PNT_NO_TARGET   (401)
#define FLOW_OFFSET     (1000000000)
#define CAPACITY_INF    (1000000001)
#define DUMMY_COST      (1)

int main(void)
{
    SQWORD sqN = inputSQWORD();

    Flow solverFlow;
    vector<SQWORD> vsqA;
    vector<SQWORD> vsqB;

    PrimeTable prime(1e7 + 1);

    map<SQWORD, SQWORD> mapB;

    for (SQWORD sqIdx = 0; sqIdx < sqN; sqIdx++) {
        SQWORD sqA = inputSQWORD();
        vsqA.emplace_back(sqA);

        mapB[sqA] ^= 1;
        mapB[sqA + 1] ^= 1;
    }

    vector<SQWORD> vsqBEven, vsqBOdd;

    for (auto b: mapB) {
        if (0 < b.second) {
            if (0 == (b.first % 2)) {
                vsqBEven.emplace_back(b.first);
            } else {
                vsqBOdd.emplace_back(b.first);
            }
        }
    }

    SQWORD sqNodeEven = 0;
    for (auto e: vsqBEven) {
        SQWORD sqNodeOdd = 0;
        for (auto o: vsqBOdd) {
            if (prime.IsPrime(abs(e - o))) {
                solverFlow.addEdge(sqNodeOdd, sqNodeOdd + PNT_NO_OFFSET, 1, DUMMY_COST);
            }
            sqNodeOdd++;
        }
        sqNodeEven++;
    }
    for (SQWORD sqIdx = 1; sqIdx < vsqBEven.size(); sqIdx++) {
        solverFlow.addEdge(PNT_NO_START, sqIdx, 1, DUMMY_COST);
    }
    for (SQWORD sqIdx = 1; sqIdx < vsqBOdd.size(); sqIdx++) {
        solverFlow.addEdge(sqIdx + PNT_NO_OFFSET, PNT_NO_TARGET, 1, DUMMY_COST);
    }



    SQWORD sqMaxFlow = solverFlow.MaxFlow(PNT_NO_START, PNT_NO_TARGET);
    SQWORD vsqCRestEven = vsqBEven.size() - sqMaxFlow;
    SQWORD vsqCRestOdd  = vsqBOdd.size() - sqMaxFlow;

    SQWORD sqAns = sqMaxFlow;    
    sqAns += ((vsqCRestEven / 2) * 2);
    sqAns += ((vsqCRestOdd / 2) * 2);
    if (0 < (vsqCRestEven %  2)) {
        sqAns += 3;
    }

    printf("%lld\n", sqAns);

    return 0;
}
