vector<int> sieve(int n) {
  vector<bool> b(n / 3 + 1, true);
  vector<int> res{2, 3};
  for (int p = 5, d = 4; p * p <= n; p += d = 6 - d) if (b[p / 3])
    for (int i = p * p, di = p % 3 * 2 * p; i <= n; i += di = 6 * p - di)
      b[i / 3] = false;
  for (int p = 5, d = 4; p <= n; p += d = 6 - d) if(b[p / 3]) res.push_back(p);
  while (not res.empty() and res.back() > n) res.pop_back();
  return res;
}

struct bipartite_matching {
  vector<vector<int>> g;
  vector<int> r, c, vis;
  int t = 0;
  bipartite_matching(int h = 0, int w = 0) : g(h), r(h, -1), c(w, -1), vis(h) {}
  void add(int i, int j) { g[i].push_back(j); }
  bool dfs(int i) {
    if (exchange(vis[i], t) == t) return false;
    for (int j : g[i]) if (c[j] == -1) return r[i] = j, c[j] = i, true;
    for (int j : g[i]) if (dfs(c[j])) return r[i] = j, c[j] = i, true;
    return false;
  }
  int run() {
    for (bool updated = true; ++t, exchange(updated, false); )
      for (int i = r.size(); i--; ) if (r[i] == -1) updated |= dfs(i);
    return r.size() - count(begin(r), end(r), -1);
  }
};

int main() {
  cin.tie(nullptr);
  ios::sync_with_stdio(false);
  int n;
  cin >> n;
  set<int> se;
  while (n--) {
    int x;
    cin >> x;
    for (int i : {x - 1, x}) {
      if (se.count(i)) {
        se.erase(i);
      } else {
        se.insert(i);
      }
    }
  }
  vector<int> even, odd;
  for (int e : se) {
    (e & 1 ? odd : even).push_back(e);
  }
  auto primes = sieve(1e7);
  int h = even.size(), w = odd.size();
  bipartite_matching bm(h, w);
  for (int i = 0; i < h; ++i) {
    for (int j = 0; j < w; ++j) {
      int d = abs(even[i] - odd[j]);
      if (binary_search(begin(primes), end(primes), d)) {
        bm.add(i, j);
      }
    }
  }
  int res = bm.run();
  h -= res, w -= res;
  if (h & 1) {
    --h, --w;
    res += 3;
  }
  res += h + w;
  cout << res << '\n';
}

