void prepare() {
	for (int i = 2; i < N; i++) {
		for (int j = 2*i; j < N; j+=i)
			is_prime[j] = true;
	}
}

bool dfs(int x) {
	for (int v : Gx[x]) {
		if(!vis[v]) {
			vis[v] = true;
			if(mat[v+R] == -1 || dfs(mat[v+R])) {
				mat[v+R] = x;
				mat[x] = v;
				return true;
			}
		}
	}
	return false;
}

int domatch() {
	memset(mat, -1, sizeof(mat));
	int cnt = 0;
	REP(i, vx.size()) {
		if(mat[i]) continue;
		memset(vis, 0, sizeof(vis));
		cnt += dfs(vx[i]);
	}
	return cnt;
}

int main() {
	prepare();
	int n, x;
	scanf("%d", &n);
	R = 110;
	REP(i, n) {
		scanf("%d", &x);
		s.insert(x);
	}
	for (int u : s) {
		if(!s.count(u-1)) e.insert(u);
		if(!s.count(u+1)) e.insert(u+1);
	}
	for (int u : e)
		if(u&1) vx.pb(u);
		else vy.pb(u);
	REP(i, vx.size())
		REP(j, vy.size())
			if(!is_prime[abs(vx[i]-vy[j])])
				Gx[i].pb(j);
	int t = domatch();
	int l1 = vx.size() - t, l2 = vy.size() - t;
	int res = t;
	res += l1/2*2;
	res += l2/2*2;
