int N;
vector<int>X;
vector<pair<int,int>>P;//first:left second:width
int dp[123];
const int SIZ = 10000001;
bool isprime[SIZ];

int main(void)
{
	cin >> N;
	for (int i = 0; i < N; ++i)
	{
		int x;
		cin >> x;
		X.push_back(x);
	}
	fill(isprime, isprime + SIZ, true);
	for (int i = 2; i < SIZ; ++i)
	{
		if (isprime[i])
		{
			for (int j = i + i; j < SIZ; j += i)
			{
				isprime[j] = false;
			}
		}
	}
	isprime[0] = isprime[1] = false;
	int i = 0;
	while (i < N) 
	{
		int left = X[i];
		int p = 1;
		while (i + 1 < N && X[i + 1] == X[i] + 1)
		{
			++i;
			++p;
		}
		P.push_back({ left, p });
		++i;
	}
	int D = P.size();
	fill(dp, dp + 123, 114514);
	dp[0] = 0;
	for (int i = 0; i < D; ++i)
	{
		//
		if (P[i].second % 2 == 0)
		{
			//=p+q
			{
				dp[i + 1] = min(dp[i + 1], dp[i] + 2);
			}
		}
		else
		{
			//
			if (isprime[P[i].second])
			{
				//p
				dp[i + 1] = min(dp[i + 1], dp[i] + 1);
			}
			else
			{
				//p+q+3(2)
				dp[i + 1] = min(dp[i + 1], dp[i] + 3);
			}
		}
		if (i + 1 != D)
		{
			//
			int left = P[i].second;
			int right = P[i + 1].second;
			int middle = P[i + 1].first - P[i].first - P[i].second;
			if (left % 2 && right % 2 == 0)
			{
				swap(left, right);
			}
			if (left % 2 && right % 2)
			{
				if (middle % 2)
				{
					bool m = isprime[middle];
					bool n = isprime[left + right + middle];
					dp[i + 2] = min(dp[i + 2], dp[i] + 2 + (m * 2) + (n * 2));
				}
				else
				{
					bool m = !isprime[middle];//2
					dp[i + 2] = min(dp[i + 2], dp[i] + 3 + m);
				}
			}
			else if (left % 2 == 0 && right % 2)
			{
				if (middle % 2)
				{
					bool m = isprime[middle];
					dp[i + 2] = min(dp[i + 2], dp[i] + 3 + (m * 2));
				}
				else
				{
					bool m = !isprime[middle];//2
					bool n = isprime[left + right + middle];
					dp[i + 2] = min(dp[i + 2], dp[i] + 2 + m + (n * 2));
				}
			}
			else
			{
				if (middle % 2)
				{
					bool m = isprime[middle];
					bool n = isprime[left + right + middle];
					dp[i + 2] = min(dp[i + 2], dp[i] + 2 + (m * 2) + (n * 2));
				}
				else
				{
					bool m = !isprime[middle];//2
					dp[i + 2] = min(dp[i + 2], dp[i] + 3 + m);
				}
			}
		}
	}
	cout << dp[D] << endl;
	return 0;
}

