void getlist(int listsize)
{
    memset(isprime,1,sizeof(isprime));
    isprime[1]=false;
    for(int i=2;i<=listsize;i++)
    {
        if(isprime[i]&&i!=2)
			prime[++primesize]=i;
		if(!isprime[i])
			continue;
		for(int j=1;j<=primesize&&i*prime[j]<=listsize;j++)
		{
            isprime[i*prime[j]]=false;
            if(i%prime[j]==0)
				break;
        }
    }
}
int main() {
	getlist(maxp-50);
	scanf("%d",&n);
	sum[++tot]=1;
	for(int i=1;i<=n;i++) {
		scanf("%d",&a[i]);
		if(i==1)
			continue;
		if(a[i]==a[i-1]+1)
			sum[tot]++;
		else
			sum[++tot]=1;
	}
	int sumx=0;
	for(int i=1;i<=tot;i++) {
		int tmp=sum[i];
		if(isprime[tmp]&&tmp!=2) {
			sumx++;
			continue;
		}
		else if(tmp%2==0) {
			sumx+=2;
			continue;
		}
		else if(tmp%2==1&&tmp!=1) {
			sumx+=3;
			continue;
		}
		else if(tmp==1) {
			sumx+=2;
			continue;
		}
	}
	printf("%d\n",sumx);
	return 0;
}

