    M[x[i]+1] ^= 1;
  }
  vector<int> even,odd;
  for(auto P : M)if(P.second==1){
    (P.first%2==0 ? even : odd).push_back(P.first);
  }
  int ne = even.size();
  int no = odd.size();
  n = ne + no + 2;
