    void init(){
        int K;
        cin>>K;
        unordered_set<int> cnt;
        vi odds,evens;
        for(int i=0;i<K;++i){
            int x;
            cin>>x;
            if(cnt.count(x)) cnt.erase(x);
            else cnt.insert(x);
            cnt.insert(x+1);
        }
        for(auto k:cnt){
            if(k%2) odds.push_back(k);
            else evens.push_back(k);
        }
        cnt.clear();
        n = (int)odds.size();
        m = (int)evens.size();
        mapping = vi(m,-1);
