        for(int i=2;i<=N;++i) if(prime[i]){
            for(int j=2;j*i<=N;++j) prime[i*j] = false;
        }
        prime[2] = false;
        for(int i=0;i<n;++i) for(int j=0;j<m;++j) E[i][j] =prime[abs(odds[i]-evens[j])];
        prime.clear();
