    if (l == r)
        return get_cost(1);
    if (dp[l][r] != -1)
        return dp[l][r];
    dp[l][r] = get_cost(1) * (r - l + 1);
    for (int k = l; k < r; ++k) {
        //if (l == 0 && r == n - 1)
        //    cerr << calc(l, k) << " " << calc(k + 1, r) << endl;
        dp[l][r] = min(dp[l][r], calc(l, k) + calc(k + 1, r));
    }
    int len = x[r] - x[l] + 1;
    int pp = lower_bound(all_p.begin(), all_p.end(), len) - all_p.begin();
    int low = 1;
    if (l != 0)
        low = x[l - 1] + 1;
    int high = INF;
    if (r != n - 1)
        high = x[r + 1];
    for (int i = pp; i <= pp + B; ++i) {
        int L = low;
        int R = x[l];
        int p = all_p[i];
        L = max(L, x[r] - p + 1);
        R = min(R, high - p);
        for (int k = L; k <= min(R, L + B); ++k) {
            vector<int> cur;
            cur.push_back(k - 1);
            for (int i = l; i <= r; i++)
                cur.push_back(x[i]);
            cur.push_back(k + p);
            int cost = 1;
            for (int i = 1; i < (int)cur.size(); ++i)
                cost += get_cost(cur[i] - cur[i - 1] - 1);
            /*if (l == 0 && r == n - 1) {
                cerr << k << " " << p << endl;
                for (int x : cur)
                    cerr << x << " ";
                cerr << cost << endl << endl;
            }*/
            dp[l][r] = min(dp[l][r], cost);
        }
    }
    return dp[l][r];
}

