template<size_t N> struct sieve {
    vi primes, least_prime, largest_proper;
    sieve() : least_prime(N + 1), largest_proper(N + 1) {
        for (int i = 2; i <= N; i++) {
            if (!least_prime[i]) {
                least_prime[i] = i;
                primes.push_back(i);
            }
            for (int x : primes) {
                if (x > least_prime[i] || i * x > N) break;
                least_prime[i * x] = x;
                largest_proper[i * x] = i;
            }
        }
    }

    bool is_prime(int v) {
        assert(0 < v && v < N);
        return least_prime[v] == v;
    }

    vi factor(int v) {
        assert(0 < v && v <= N);
        vi res;
        while (v > 1) {
            res.push_back(least_prime[v]);
            v = largest_proper[v];
        }
        reverse(all(res));
        return res;
    }
};

int main() {
    setIO();

    int N; re(N);

    const int MAXV = 1e7 + 1;
    sieve<MAXV> sv;

    vb cards(MAXV);
    F0R (i, N) { int v; re(v); cards[v] = true; }

    vi even, odd;
    F0R (i, MAXV - 1) if (cards[i] != cards[i+1]) {
        (i&1 ? odd : even).pb(i);
    }

    dinic<int> g(1 + sz(even) + sz(odd) + 1);
    F0R (i, sz(even)) g.arc(0, i+1, 1);
    F0R (j, sz(odd)) g.arc(1+sz(even)+j, g.V-1, 1);
    F0R (i, sz(even)) {
        F0R (j, sz(odd)) {
            if (sv.is_prime(abs(even[i] - odd[j])))
                g.arc(1+i, 1+sz(even)+j, 1);
        }
    }

    int m = get<0>(g.max_flow(0, g.V-1));

    int ans = (sz(even) - m)&1;
    ans += sz(even) + sz(odd) - m;
    ps(ans);

    return 0;
}

