const int MX = 205;

int D[MX];
vector<int> L[MX];
int R[MX];
int vst[MX], t = 1;

int dfs(int x){
	if( vst[x] == t ) return 0;
	vst[x] = t;
	for(int c : L[x]){
		if( !R[c] || dfs(R[c])){
			R[c] = x;
			return 1;
		}
	}
	return 0;
}

int is_prime(int a){
	if( a < 0) a = -a;
	if( a == 1 ) return false;
	for(int i = 2; i*i <= a; i++) if( a%i == 0 ) return false;
	return true;
}

int main()
{
	int N;
	scanf("%d", &N);
	for(int i = 1; i <= N; i++){
		scanf("%d", D+i);
	}

	int M = 1, E[MX] = {0, D[1]};
	for(int i = 2; i <= N; i++){
		if( D[i-1] != D[i]-1){
			E[++M] = D[i-1]+1;
			E[++M] = D[i];
		}
	}
	E[++M] = D[N]+1;

	int a = 0, b = 0;
	for(int i = 1; i <= M; i++){
		if( E[i]%2 == 0) a++;
		else b++;
	}
	for(int i = 1; i <= M; i++){
		for(int j = 1; j <= M; j++){
			if( E[i]%2 == 0 && E[j]%2 == 1 );
			else continue;
			if( is_prime(E[i] - E[j]) ) L[i].push_back(j);
		}
	}
	int ans = 0;
	for(int i = 1; i <= M; i++){
		t++; if(dfs(i)) ans++, a--, b--;
	}
	ans += (a/2 + b/2) * 2;
	if( a%2 == 1 ) ans += 3;
	printf("%d\n", ans);
}

