long long ksc(long long u,long long v,long long n){
	return (u*v-(long long)((long double)u/n*v)*n+n)%n;
}
long long ksm(long long u,long long v,long long n){
	long long o=1;
	while(v){
		if(v&1)
			o=ksc(o,u,n);
		u=ksc(u,u,n);
		v>>=1;
	}
	return o;
}
long long miller(long long n){
	if(n<=28){
		if(n==2||n==3||n==5||n==7||n==11||n==13||n==17||n==19||n==23)
			return 1;
		return 0;
	}
	long long book[9]={2,3,5,7,11,13,17,19,23},i,j,x,y;
	for(i=0;i<=8;i++){
		j=n-1;
		while((j&1)==0)
			j>>=1;
		if(ksm(book[i],n-1,n)!=1)
			return 0;
		x=ksm(book[i],j,n);
		if(x!=1){
			while(j!=n-1){
				if(x==n-1)
					break;
				if(x==1)
					return 0;
				x=ksc(x,x,n);
				j<<=1;
			}
			if(x!=n-1)
				return 0;
		}
	}
	return 1;
}
