typedef long long ll;typedef vector<int> vi;typedef pair<int,int> pi;const int inf = (int)1e9;const double INF = 1e12, EPS = 1e-9;
struct FlowGraph{
	struct edge{ int to, cap, rev; };

	int n, *level, *iter;
	vector<vector<edge> > G;

	FlowGraph(int sz) : n(sz){
		G.resize(n);
		iter = new int[n]; level = new int[n];
	}
	~FlowGraph(){
		delete [] iter; delete [] level;
	}

	void add(int s, int t, int cap){
		G[s].pb((edge){t, cap, (int)G[t].size()});
		G[t].pb((edge){s, 0, (int)G[s].size() - 1});
	}
	void bfs(int s){
		rep(i, n) level[i] = -1;
		queue<int> q;
		level[s] = 0;
		q.push(s);
		while(!q.empty()){	
			int v = q.front();
			q.pop();
			rep(i, G[v].size()){
				edge &e = G[v][i];
				if(e.cap > 0 && level[e.to] < 0){
					level[e.to] = level[v] + 1;
					q.push(e.to);
				}
			}
		}
	}
	int dfs(int v, int t, int f){
		if(v == t) return f;
		for(int &i = iter[v]; i < (int)G[v].size(); i++){
			edge &e = G[v][i];
			if(e.cap > 0 && level[v] < level[e.to]){
				int d = dfs(e.to, t, min(f, e.cap));
				if(d > 0){
					e.cap -= d;
					G[e.to][e.rev].cap += d;
					return d;
				}
			}
		}
		return 0;
	}
	int max_flow(int s, int t){
		int flow = 0;
		while(1){
			bfs(s);
			if(level[t] < 0) return flow;
			rep(i, n) iter[i] = 0;
			int f;
			while((f = dfs(s, t, inf)) > 0) flow += f;
		}
	}
};
int main(){
	cin.tie(0); cin.sync_with_stdio(0);

	int n; cin >> n;
	vi v(n), u; rep(i, n) cin >> v[i];
	int cnt = 0;
	rep(i, n){
		if(i && v[i] != v[i - 1] + 1){
			u.pb(v[i - 1] + 1);
			u.pb(v[i - 1] + 1 - cnt);
			cnt = 1;
		}
		else cnt++;
	}
	u.pb(v[n - 1] + 1);
	u.pb(v[n - 1] + 1 - cnt);

	auto isPrime = [](int x){
		for(int i = 2; i * i <= x; i++) if(x % i == 0) return 0;
		return 1;
	};

	vi o, e;
	for(int i : u) if(i % 2) o.pb(i); else e.pb(i);
	int s = o.size() + e.size(), t = s + 1;
	FlowGraph g(o.size() + e.size() + 2);
	{
		rep(i, o.size()) rep(j, e.size()) if(isPrime(abs(o[i] - e[j]))) g.add(i, j + o.size(), 1);
		rep(i, o.size()) g.add(s, i, 1);
		rep(i, e.size()) g.add(i + o.size(), t, 1);
	}
	int f = g.max_flow(s, t);

	int ans = f + ((o.size() - f) / 2 + (e.size() - f) / 2) * 2 + (o.size() - f) % 2 * 3;
	cout << ans << endl;
	return 0;
}

