		for(int j=1; j<=n; ++j){
			if(i == j) continue;
			if(i<j){
				dist[i][j]=get_dist(i, j);
				pq.emplace(-dist[i][j], i, j);
			}
			else dist[i][j]=dist[j][i];
		}
	}
	int ans = 0;
	while(pq.size()){
		int d, x, y; tie(d,x,y) = pq.top(); pq.pop();
