				int x=aite[i], y=aite[j];
				int df = dist[i][x] + dist[j][y] - dist[i][y] - dist[j][x];
				if(df > 0){
					ans -= df;
					aite[i]=y; aite[y]=i;
					aite[j]=x; aite[x]=j;
					rel=1;
					break;
				}
			}
