    for(int i = 2;i <= 10000000;i++)
    {
        if(!pp[i])
            pp[++pp[0]] = i;
        for(int j = 1;j <= pp[0]&&10000000 / i >= pp[j];j++)
        {
            pp[i * pp[j]] = 1;
            if(i % pp[j] == 0)
                break;
        }
    }
}

bool dfs(int u)
{
    int lim = eg[u].size();
    for(int i = 0;i < lim;i++)
    {
        int v = eg[u][i];
        if(!vis[v])
        {
            vis[v] = 1;
            if(lk[v] == -1||dfs(lk[v]))
            {
                lk[v] = u;
                return true;
            }
        }
    }
    return false;
}

int main()
{
    inits();
    int mx = 0;
    scanf("%d", &n);
    for(int i = 1;i <= n;i++)
        scanf("%d", &num[i]);
    sort(num + 1, num + 1 + n);
    num2[++nn] = num[1];
    for(int i = 2;i <= n;i++)
    {
        if(num[i] - 1 != num[i - 1])
        {
            num2[++nn] = num[i - 1] + 1;
            num2[++nn] = num[i];
        }
    }
    num2[++nn] = num[n] + 1;
    mx = num[n] + 1;
    for(int i = 1;i <= nn;i++)
    {
        if(num2[i] % 2 == 0)
        {
            ou++;
