        }
        else
            ji++;
        for(int j = 2;j <= pp[0];j++)
        {
            if(num2[i] + pp[j] > mx)
                break;
            if(mps.find(num2[i] + pp[j]) != mps.end())
            {
                int pos = mps[num2[i] + pp[j]];
                eg[i].push_back(pos);
            }
        }
    }
