inline bool isp(int n)
{
	for(int i = 2; i * i <= n; ++i) if(n % i == 0) return 0;
	return 1;
}

int calc(int n)
{
	if(n % 2 == 0) return 2;
	if(n == 1) return 3;
	if(isp(n)) return 1;
	return 3;
}

