        }
        for (auto c: vsqCOdd) {
            auto it = lower_bound(vsqCEven.begin(), vsqCEven.end(), c + p);
            if (it != vsqCEven.end()) {
                if (*it == c + p) {
                    if (c + p < 1e7 + 1) {
                        solverFlow.addEdge(mapCEven[c + p], mapCOdd[c] + PNT_NO_OFFSET, 1, DUMMY_COST);
                    }
                }
            }
        }
    }



    SQWORD sqMaxFlow = solverFlow.MaxFlow(PNT_NO_START, PNT_NO_TARGET);
    SQWORD vsqCRestEven = 0;
    SQWORD vsqCRestOdd = 0;
    for (auto c: vsqCEven) {
        SQWORD sqFlow = solverFlow.getFlow(mapCEven[c], true);
        if (0 == sqFlow) {
            vsqCRestEven++;
        }
    }
    for (auto c: vsqCOdd) {
        SQWORD sqFlow = solverFlow.getFlow(mapCOdd[c] + PNT_NO_OFFSET, false);
        if (0 == sqFlow) {
            vsqCRestOdd++;
        }
    }

    SQWORD sqAns = sqMaxFlow;    
    sqAns += (vsqCRestEven / 2) * 2;
    sqAns += (vsqCRestOdd / 2) * 2;
    if (0 < (vsqCRestEven %  2)) {
        sqAns += 3;
    }

    printf("%lld\n", sqAns);

    return 0;
}
