bool prime(int x) {
    if (x == 1) return false;
    for (int i = 2; i * i <= x; i++) {
        if (x % i == 0) return false;
    }
    return true;
}

bool dfs(int x) {
    if (z[x]) return false;
    z[x] = true;
    for (int y = 0; y < nr; y++) {
        if (a[x][y]) {
            if (p[y] == -1 || dfs(p[y])) {
                p[y] = x;
                return true;
            }
        }
    }
    return false;
}

int main() {
    std::ios::sync_with_stdio(false);

    int n;
    cin >> n;

    int x[n];
    for (int i = 0; i < n; i++) { cin >> x[i]; }

    vector<int> l;
    vector<int> r;

    for (int i = 0; i < n; i++) {
        if (i == 0 || x[i - 1] != x[i] - 1) {
            (x[i] % 2 == 0 ? l : r).push_back(x[i]);
        }
        if (i == n - 1 || x[i + 1] != x[i] + 1) {
            ((x[i] + 1) % 2 == 0 ? l : r).push_back(x[i] + 1);
        }
    }

    nl = l.size();
    nr = r.size();
    a.resize(nl);
    for (int i = 0; i < nl; i++) {
        a[i].resize(nr);
        for (int j = 0; j < nr; j++) {
            int d = abs(l[i] - r[j]);
            a[i][j] = prime(d);
        }
    }

    p.resize(nr);
    fill(p.begin(), p.end(), -1);
    z.resize(nl);
    int res = 0;
    for (int i = 0; i < nl; i++) {
        fill(z.begin(), z.end(), false);
        if (dfs(i)) {
            res++;
        }
    }

    nr -= res;
    nl -= res;

    int ans = nr + nl + res;
    if (nr % 2 == 1) {
        ans++;
    }

    cout << ans;

    return 0;
}

