struct CostFlow {
	static const int MAX_N = 1e3 + 6;
	struct Edge {
		int to,cap,rev,cost;
	};
	vector<Edge> edg[MAX_N];
	#define SZ(x) ((int)(x).size())
	void add_edge(int from,int to,int cap,int cost) {
		edg[from].push_back({to,cap,SZ(edg[to]),cost});
		edg[to].push_back({from,0,SZ(edg[from])-1,-cost});
	}
	int n,s,t;
	void init(int _n,int _s,int _t) {
		n=_n;
		s=_s;
		t=_t;
		for (int i=0;n>=i;i++) {
			edg[i].clear();
		}
	}
	static const int INF = 1e9 + 7;
	int dis[MAX_N];
	int pre[MAX_N],pre_id[MAX_N];
	bool in_que[MAX_N];
	pii flow() {
		int cost=0;
		int flow=0;
		while (true) {
//			cout<<"in"<<endl;
			for (int i=0;n>=i;i++) {
				dis[i] = INF;
				in_que[i]=0;
			}
			queue<int> que;
			que.push(s);
			dis[s] = 0;
			while (!que.empty()) {
				int t=que.front();
				que.pop();
				int id=0;
				in_que[t]=0;
				for (Edge e:edg[t]) {
					if (e.cap > 0 && dis[e.to] > dis[t] + e.cost) {
						dis[e.to] = dis[t] + e.cost;
						pre[e.to] = t;
						pre_id[e.to] = id;
						if (!in_que[e.to]) {
							in_que[e.to]=1;
							que.push(e.to);
						}
					}
					id++;
				}
			}
//			cout<<"haha"<<endl;
			if (dis[t] == INF || dis[t] == 3) break;
			int mn_flow = INF;
			for (int i=t;i!=s;i=pre[i]) {
//				cout<<"i = "<<i<<endl;
				mn_flow = min(mn_flow,edg[pre[i]][pre_id[i]].cap);
			}
//			cout<<"finish "<<endl;
			flow += mn_flow;
			cost += dis[t]*mn_flow;
			for (int i=t;i!=s;i=pre[i]) {
				edg[pre[i]][pre_id[i]].cap -= mn_flow;
				edg[i][edg[pre[i]][pre_id[i]].rev].cap += mn_flow;
			}
//			cout<<"finish2"<<endl;
		}
		return make_pair(cost,flow);
	}
} Flow;

int p[MAX_N];
int q[MAX_N];

typedef long long LL;
const int MAX_P = 1e7 + 6;

int prime[MAX_P];

void build() {
	prime[0] = prime[1]=1;
	for (LL i=2;MAX_P>i;i++) {
		if (prime[i]==0) {
			for (LL j=i*i;MAX_P>j;j+=i) {
				prime[j]=1;
			}
		}
	}
}

bool isPrime(int x) {
	return prime[x]?false:true;
}

