	G.resize(X + Y + 2);
	rep(i, X) add_edge(0, i + 1, 1);
	rep(i, Y) add_edge(i + X + 1, X + Y + 1, 1);
	rep(i, X) {
		rep(j, Y) {
			if (is_prime[abs(even[i] - odd[j])]) add_edge(i + 1, j + X + 1, 1);
		}
	}
	int k = max_flow(0, X + Y + 1);
	int ans = k + (X - k) / 2 * 2 + (Y - k) / 2 * 2 + ((X - k) % 2) * 3;
	cout << ans << endl;
}

