    if (i == 1 || x[i - 1] != x[i] - 1) pos[++ m] = x[i];
    if (i == n || x[i + 1] != x[i] + 1) pos[++ m] = x[i] + 1;
  }
  make_prime(pos[m] - pos[1]);
  for (int i = 1; i <= m; i ++) {
    if (pos[i] & 1) continue;
    for (int j = 1; j <= m; j ++)
      if ((pos[j] & 1) && !notp[abs(pos[j] - pos[i])]) solver.Add(i, j);
  }
  int k = solver.MaxMatch(), ev = 0, od = 0;
  for (int i = 1; i <= m; i ++) {
    if (pos[i] & 1) od ++;
    else ev ++;
  }
  ev -= k; od -= k;
  int ans = k + (ev / 2 + od / 2) * 2 + 3 * (ev & 1);
  printf("%d\n", ans);
  return 0;
}

