        if (i == N - 1 || arr[i+1] - 1 > arr[i])
            v.push_back(arr[i]+1);
    }

    int m = v.size();
    Dinic d (m + 2);
    int ne = 0, no = 0;
    for (int i = 0; i < m; i++)
    {
        if (v[i] % 2 == 0)
        {
            d.addEdge (m, i, 1);
            ne++;
        }
        else
        {
            d.addEdge (i, m + 1, 1);
            no++;
        }
    }
    for (int i = 0; i < m; i++)
        for (int j = 0; j < m; j++)
        {
            if (v[i] % 2 == 0 && v[j] % 2 == 1)
            {
