void init(){
	flag[1]=1;
	for(int i=2;i<MAXM;i++){
		if(!flag[i]) prime[++nump]=i;
		for(int j=1;j<=nump && i*prime[j]<MAXM;j++){
			flag[i*prime[j]]=1;
			if(i%prime[j]==0) break;
		}
	}
	flag[2]=1;
}

bool bfs(){
	memset(level,-1,sizeof level);
	static queue<int> Q;
	Q.push(S);
	level[S]=0;
	while(!Q.empty()){
		int x=Q.front();
		Q.pop();
		for(int i=g[x];~i;i=e[i].next)
			if(e[i].w && level[e[i].to]==-1){
				level[e[i].to]=level[x]+1;
				Q.push(e[i].to);
			}
	}
	return level[T]!=-1;
}

int dfs(int x,int delta){
	if(x==T) return delta;
	if(!delta) return 0;
	int res=0;
	for(int i=g[x];~i;i=e[i].next)
		if(e[i].w && level[e[i].to]==level[x]+1){
			int temp=dfs(e[i].to,min(delta,e[i].w));
			delta-=temp;
			res+=temp;
			e[i].w-=temp;
			e[i^1].w+=temp;
			if(!delta) return res;
		}
	return res;
}

