  BPM::n = SZ(b[0]);
  BPM::m = SZ(b[1]);
  REP(i, BPM::n) REP(j, BPM::m) {
    int d = abs(b[0][i] - b[1][j]);
    if (d >= 3 && !Sieve::vis[d]) BPM::g[i].PB(j);
  }
  int k = BPM::solve();
  int ans = k + ((BPM::n - k) / 2 + (BPM::m - k) / 2) * 2 + ((BPM::n - k) % 2) * 3;
  printf("%d\n", ans);
  return 0;
}

