struct edge { int to, cap, rev; };
vector<edge> G[100000];
bool used[100000];
void add_edge(int from, int to, int cap) {
	G[from].push_back(edge{ to, cap, (int)G[to].size() });
	G[to].push_back(edge{ from, 0, (int)G[from].size() - 1 });
}
int dfs(int v, int t, int f) {
	if (v == t)return f;
	used[v] = true;
	for (int i = 0; i < (int)G[v].size(); i++) {
		edge &e = G[v][i];
		if (!used[e.to] && e.cap > 0) {
			int d = dfs(e.to, t, min(f, e.cap));
			if (d > 0) {
				e.cap -= d;
				G[e.to][e.rev].cap += d;
				return d;
			}
		}
	}
	return 0;
}
int max_flow(int s, int t) {
	int flow = 0;
	for (;;) {
		memset(used, 0, sizeof(used));
		int f = dfs(s, t, mod);
		if (f == 0)return flow;
		flow += f;
	}
}

const int mn = 10000000;
bool isp[mn + 1];
void init() {
	fill(isp + 2, isp + mn + 1, true);
	Rep(i, 2, mn + 1) {
		if (!isp[i])continue;
		for (int j = 2 * i; j <= mn; j += i) {
			isp[j] = false;
		}
	}
}
void solve() {
	int n; cin >> n;
	vector<int> x(n);
	rep(i, n) {
		cin >> x[i];
	}
	vector<int> v;
	rep(i, n) {
		if (i == 0 || x[i] - 1 > x[i - 1]) {
			v.push_back(x[i] - 1);
		}
		if (i + 1 == n || x[i + 1] > x[i] + 1) {
			v.push_back(x[i]);
		}
	}
	vector<int> od, ev;
	rep(i, v.size()) {
		//cout << v[i] << endl;
		if (v[i] % 2)od.push_back(v[i]);
		else ev.push_back(v[i]);
	}
	int s = od.size() + ev.size();
	int t = s + 1;
	rep(i, od.size()) {
		add_edge(s, i, 1);
	}
	rep(i, ev.size()) {
		add_edge(i + od.size(), t, 1);
	}
	rep(i, od.size()) {
		rep(j, ev.size()) {
			int l = i;
			int r = od.size() + j;
			int dif = abs(od[i] - ev[j]);
			if (isp[dif]) {
				add_edge(l, r, 1);
			}
		}
	}
	int f = max_flow(s, t);
	int ans = f;
	int odnum = od.size() - f;
	ans += 2*(odnum / 2);
	int evnum = ev.size() - f;
	ans += 2*(evnum / 2);
	if (odnum % 2||evnum%2) {
		ans += 3;
	}
	cout << ans << endl;
}

