                b.pb(vec[i]);
            }
        }
        if(i < n-1){
            if(vec[i+1] != vec[i]+1){
                b.pb(vec[i]+1);
            }
        }
    }
    b.pb(vec[n-1]+1);
    vector<int> ev,od;
    rep(i,b.size()){
        if(b[i]%2 == 0){
            ev.pb(b[i]);
        }else{
