void add(int x, int y, int z) {
	ter[++ e] = y, nxt[e] = lnk[x], lnk[x] = e, w[e] = z, rec[e] = e + 1 ;
	ter[++ e] = x, nxt[e] = lnk[y], lnk[y] = e, w[e] = 0, rec[e] = e - 1 ;
}

bool bfs() {
	rep(i, s, t) dis[i] = inf ;
	dis[s] = 0 ;
	int he = 0, ta = 1 ;
	opt[1] = s ;
	for ( ; he != ta ; ) {
		++ he ; 
		int u = opt[he] ;
		for (int i = lnk[u]; i; i = nxt[i]) if (dis[ter[i]] == inf && w[i]) {
			dis[ter[i]] = dis[u] + 1 ;
			opt[++ ta] = ter[i] ;
		}
	}
	return dis[t] < inf ;
}

int dfs(int p, int sum) {
	if (p == t) return sum ;
	int res = 0 ;
	for (int i = cur[p] ; i; i = nxt[i]) if (dis[ter[i]] == dis[p] + 1 && w[i]) {
		int tmp = dfs(ter[i], min(sum - res, w[i])) ;
		res += tmp, w[i] -= tmp, w[rec[i]] += tmp ;
		if (w[i]) cur[p] = i ;
		if (res == sum) return res ;
	}
	return res ;
}

int dinic() {
	int res = 0 ;
	for ( ; bfs() ; ) {
		rep(i, s, t) cur[i] = lnk[i] ;
		res += dfs(s, inf) ;
	}
	return res ;
}

