inline bool Judge(rg int Num)
{   if(Num%2==0||Num<=2) return 0;
	for(rg int i=3,End=sqrt(Num);i<=End;i+=2)
		if(Num%i==0) return 0;
	return 1;
}
inline bool Couple(rg int Now,rg int Sign)
{   for(rg int i=First[Now];i!=-1;i=Edge[i].Next)
	{   rg int v=Edge[i].v;   if(Vis[v]==Sign) continue ;
		Vis[v]=Sign;
		if(!Match[v]||Couple(Match[v],Sign)) return Match[v]=Now,Match[Now]=v,1;
	}
	return 0;
}
