using namespace std;
#define SIZE 1002
vector<int>pat[SIZE];
vector<int>cap[SIZE];
vector<int>cost[SIZE];
vector<int>rev[SIZE];
void adde(int a, int b, int cp, int cst)
{
	pat[a].push_back(b);
	pat[b].push_back(a);
	cap[a].push_back(cp);
	cap[b].push_back(0);
	cost[a].push_back(cst);
	cost[b].push_back(-cst);
	rev[a].push_back(pat[b].size() - 1);
	rev[b].push_back(pat[a].size() - 1);
}
int dist[SIZE];
int nd[SIZE];
typedef pair<int, int>pii;
int frv[SIZE], fre[SIZE];
int nod;
int mincostflow(int st, int go, int gen)
{
	int ret = 0;
	fill(nd, nd + SIZE, 0);
	for (;;)
	{
		if (gen == 0)return ret;
		priority_queue<pii>que;
		que.push(make_pair(0, st));
		fill(dist, dist + nod, 1000000000);
		for (;;)
		{
			if (que.empty())break;
			pii zan = que.top();
			que.pop();
			if (dist[zan.second]<-zan.first)continue;
			dist[zan.second] = -zan.first;
			for (int i = 0; i<pat[zan.second].size(); i++)
			{
				if (cap[zan.second][i]>0 && dist[pat[zan.second][i]]>dist[zan.second] + cost[zan.second][i] + nd[zan.second] - nd[pat[zan.second][i]])
				{
					dist[pat[zan.second][i]] = dist[zan.second] + cost[zan.second][i] + nd[zan.second] - nd[pat[zan.second][i]];
					frv[pat[zan.second][i]] = zan.second;
					fre[pat[zan.second][i]] = i;
					que.push(make_pair(-dist[pat[zan.second][i]], pat[zan.second][i]));
				}
			}
		}
		if (dist[go] == 1000000000)return -1;
		for (int i = 0; i<nod; i++)nd[i] += dist[i];
		int fl = gen;
		int now = go;
		for (;;)
		{
			if (now == st)break;
			int t = fre[now];
			now = frv[now];
			fl = min(fl, cap[now][t]);
		}
		gen -= fl;
		ret += fl*nd[go];
		now = go;
		for (;;)
		{
			if (now == st)break;
			int t = fre[now];
			int nx = now;
			now = frv[now];
			cap[now][t] -= fl;
			cap[nx][rev[now][t]] += fl;
		}
	}
	return ret;
}
