using namespace std;

const int Nmax = 205, lim = 2e7 + 5;

int n, a[Nmax];
bitset<lim+3> prim;


class Graph
{
    vector<int> edge[Nmax];
    bool marked[Nmax];
    int L[Nmax], R[Nmax];

    bool cupleaza(int node)
    {
        if(marked[node]) return 0;
        marked[node] = 1;

        for(auto it : edge[node])
            if(R[it] == -1)
            {
                L[node] = it;
                R[it] = node;
                return 1;
            }

        for(auto it : edge[node])
            if(cupleaza(R[it]))
            {
                L[node] = it;
                R[it] = node;
                return 1;
            }
        return 0;
    }

public:
    void add_edge(int x, int y)
    {
        edge[x].push_back(y);
    }

    int cuplaj(int n, int m)
    {
        int done = 1, i;
        for(i=0; i<n; ++i) L[i] = -1;
        for(i=0; i<m; ++i) R[i] = -1;

        while(done)
        {
            done = 0;
            memset(marked, 0, sizeof(marked));

            for(i=0; i<n; ++i)
                if(L[i] == -1) done |= cupleaza(i);
        }

        int cnt = 0;
        for(i=0; i<n; ++i) cnt += (L[i] != -1);
        return cnt;
    }

} graph;

void prec()
{
    int i, j;

    prim.set();
    for(i=2; i*i <= lim; ++i)
        if(prim[i])
            for(j=i*i; j<=lim; j+=i)
                prim[j] = 0;
}

