        p.push_back(a[i]);
        p.push_back(a[j-1] + 1);

        i = j - 1;
    }

    for(auto it : p)
        if(it & 1) impar.push_back(it);
            else par.push_back(it);

    for(i=0; i<impar.size(); ++i)
        for(j=0; j<par.size(); ++j)
            if(prim[impar[i] + par[j]]) graph.add_edge(i, j);

    int best = graph.cuplaj(impar.size(), par.size()), A, B;

    A = impar.size() - best;
    B = par.size() - best;

