		scanf("%d", a + i);

	return true;
}

bool dfs(int v)
{
	if (used[v])
		return false;

	used[v] = true;

	for (auto to : g[v])
		if (mt[to] == -1 || dfs(to))
			return true;

	return false;
}

void solve()
{
	for (int i = 2; i < maxn; ++i)
		pr[i] = true;

	for (int i = 2; i < maxn; ++i)
		if (pr[i] && 1ll * i * i < maxn)
			for (int j = i * i; j < maxn; j += maxn)
				pr[j] = false;

	pr[2] = false;
	set<int> s;

	for (int i = 0; i < n; ++i)
	{
		s.insert(a[i] + 1);

		if (s.count(a[i]))
			s.erase(a[i]);
		else
			s.insert(a[i]);
	}

	vector<int> v0, v1;

	for (auto i : s)
		i % 2 == 0 ? v0.push_back(i) : v1.push_back(i);

	for (int i = 0; i < size(v0); ++i)
		for (int j = 0; j < size(v1); ++j)
			if (pr[abs(v0[i] - v1[j])])
				g[i].push_back(j);

	for (int i = 0; i < size(v0); ++i)
		mt[i] = -1;

	for (int i = 0; i < size(v0); ++i)
	{
		fill(used, used + size(v0), false);
		dfs(i);
	}

	int matching = 0;

	for (int i = 0; i < size(v0); ++i)
		matching += mt[i] != -1;

	int ans = matching;
	ans += 2 * ((size(v0) - matching) / 2);
	ans += 2 * ((size(v1) - matching) / 2);
	ans += 3 * ((size(v0) - matching) % 2);
	printf("%d\n", ans);
}

