{
	int n = read ();
	int Maxn = 0;
	for (Int i = 1;i <= n;++ i)
	{
		int x = read ();
		rev[x] ^= 1;
		Maxn = max (Maxn,x + 1);
	}
	Prime (Maxn);
	for (Int i = 1;i <= Maxn;++ i)
		if (rev[i] != rev[i - 1])
			if (i & 1)
				s1.push_back(i);
			else 
				s2.push_back(i);
	for (Int i = 0;i < s1.size();++ i)
		for (Int j = 0;j < s2.size();++ j)
			if (check (Abs (s1[i] - s2[j])))
				Add_Edge (i,j);
	int Ans = Hungry ();
	write (Ans + (s1.size() - Ans) / 2 * 2 + (s2.size() - Ans) / 2 * 2 + (s1.size() - Ans) % 2 * 3),putchar ('\n'); 
	return 0; 
}

