	memset(isprime,true,sizeof(isprime));
	for(int i=2;i<=batasprima;i++)
	{
		if(!isprime[i])
			continue;
		prime.pb(i);
		for(int j=i+i;j<=batasprima;j+=i)
			isprime[j]=false;
	}
}
bool isprime(int angka){
	if(angka<2)
		return false;
	for(auto isi:prime)
	{
		if(angka%isi==0)
			return false;
	}
	return true;
}
bool matching(int pos){
	if(visited[pos])
		return false;
	visited[pos]=true;
	for(auto isi:adj[pos])
	{
		if(match[isi]==-1||matching(match[isi]))
		{
			match[isi]=pos;
			match[pos]=isi;
			return true;
		}
	}
	return false;
}
