il Init(){
	is_not[1]=1;
	for(ri i=2;i<=MAXN-5;++i){
		if(!is_not[i]) pri[++ccnt]=i;
		for(ri j=1;j<=ccnt&&i*pri[j]<=MAXN-5;++j){
			is_not[i*pri[j]]=1;
			if(i%pri[j]==0) break;
		}
	}
}
int num_edge=-1,head[N],cur[N],dis[N],cntL,cntR,L[N],R[N];
struct Edge{ int next,to,w; }edge[M];
il add_edge(int u,int v,int w){
	edge[++num_edge]=(Edge){ head[u],v,w },head[u]=num_edge;
	edge[++num_edge]=(Edge){ head[v],u,0 },head[v]=num_edge;
}
inl bool BFS(int s,int t){
	for(ri i=s;i<=t;++i) dis[i]=0;
	queue<int> q;q.push(s),dis[s]=1;
	while(!q.empty()){
		int pos=q.front();q.pop();
		for(ri i=head[pos];i!=-1;i=edge[i].next)
			if(edge[i].w>0&&!dis[edge[i].to]){
				dis[edge[i].to]=dis[pos]+1;
				if(edge[i].to==t) return true;
				q.push(edge[i].to);
			}
	}
	return false;
}
it DFS(int now,int t,int flow){
	if(now==t) return flow;
	int s=0,k;
	for(ri &i=cur[now];i!=-1;i=edge[i].next) if(edge[i].w>0&&dis[edge[i].to]==dis[now]+1){
		k=DFS(edge[i].to,t,min(flow-s,edge[i].w));
		s+=k,edge[i].w-=k,edge[i^1].w+=k;
		if(s==flow) break;
	}
	if(!s) dis[now]=0;
	return s;
}
it Dinic(int s,int t){
	int res=0;
	while(BFS(s,t)){
		for(ri i=s;i<=t;++i) cur[i]=head[i];
		res+=DFS(s,t,INF);
	}
	return res;
}
