int add(int a, int b, int c)
{
	to[tot] = b;
	w[tot] = c;
	nxt[tot] = h[a];
	h[a] = tot;
	tot++;
	to[tot] = a;
	nxt[tot] = h[b];
	h[b] = tot;
	return 0;
}
int bfs()
{
	memcpy(cur, h, sizeof cur);
	memset(num, 0, sizeof num);
	q.push(s);
	num[s] = 1;
	while (!q.empty())
	{
		int x = q.front();
		q.pop();
		for (int i = h[x]; i != -1; i = nxt[i])
			if (w[i] && !num[to[i]])
			{
				num[to[i]] = num[x] + 1;
				q.push(to[i]);
			}
	}
	return num[t];
}
int dfs(int x, int l)
{
	if (x == t)return l;
	int used = 0;
	for (int& i = cur[x]; i != -1; i = nxt[i])
		if (w[i] && num[to[i]] == num[x] + 1)
		{
			int d = dfs(to[i], min(w[i], l - used));
			if (d)
			{
				w[i] -= d;
				w[i ^ 1] += d;
				used += d;
				if (l == used)break;
			}
		}
	return used;
}
int main()
