		if(up[i]!=up[i-1])
			i&1 ? x_id[++cnt_x]=i:y_id[++cnt_y]=i;
	}
	build();
	int k=Dinic((cnt_x+cnt_y)|1,cnt_x+cnt_y+2,cnt_x+cnt_y+2);
	printf("%d",k+((((cnt_x-k)>>1)+((cnt_y-k)>>1))<<1)+(cnt_x-k)%2*3);
	return 0;
}

