bool bfs(){
	for(int i=1;i<=T;++i)h[i]=0;
	int ql=0,qr=0;
	h[q[++qr]=S]=1;
	while(ql!=qr){
		int w=q[++ql];
		if(w==T)return 1;
		for(int i=e0[w];i;i=e[i].nx){
			int u=e[i].to;
			if(!h[u])h[q[++qr]=u]=h[w]+1;
		}
	}
	return 0;
}
int dfs(int w,int f){
	if(w==T)return f;
	int c,used=0;
	for(int i=e0[w];i;i=e[i].nx){
		int u=e[i].to;
		if(h[u]!=h[w]+1)continue;
		c=dfs(u,std::min(e[i].v,f-used));
		e[i].v-=c;
		e[i^1].v+=c;
		used+=c;
		if(f==used)return f;
	}
	h[w]=0;
	return used;
}
