const int N=205,L=1e7+5;
bool mark[L],prime[L];
namespace match
{
  const int N=205;
  int l[N],r[N],n,m;
  bool a[N][N],seen[N];
  bool findmatch(int i)
  {
    rep(j,0,m)
      {
	if(a[i][j] && !seen[j])
	  {
	    seen[j]=true;
	    if(r[j]<0 || findmatch(r[j]))
	      {
		l[i]=j; r[j]=i;
		return true;
	      }
	  }
      }
    return false;
  }
  int bipartitematch()
  {
    int cnt=0;
    rep(i,0,n) l[i]=-1;
    rep(i,0,m) r[i]=-1;
    rep(i,0,n)
      {
	rep(j,0,m) seen[j]=false;
	if(findmatch(i)) cnt++;
      }
    return cnt;
  }
};
