//{{{ minCostFlow(Graph<Flow,Cost> g,{s,t},f)
template<class Flow,class Cost>
struct minCostFlow_t{
	vector<vector<Flow> > flow;
	bool feasible;
	Cost val;
	operator bool(){return feasible;}
	Cost operator*(){return val;}
};

template<class Flow,class Cost>
minCostFlow_t<Flow,Cost> minCostFlow(const Graph<pair<Flow,Cost> > &g,const pair<int,int> &st,Flow f){
	const int n = g.size();
	const int s = st.first, t = st.second;
	minCostFlow_t<Flow,Cost> ret;
	vector<vector<Flow> > &flow = ret.flow;
	flow.assign(n,vector<Flow>());
	const Flow FINF=1e+9;
	const Cost CINF=1e+18;

	REP(i,n)REP(j,g[i].size())flow[i].push_back(g[i][j].weight.first);
	vector<Cost> dist(n);
	vector<int> prevv(n),preve(n);
	Cost res=0;
	while(f>0){
		fill(ALL(dist),CINF);
		dist[s]=0;
		bool update=true;
		while(update){
			update=false;
			for(int v=0;v<n;v++){
				if(dist[v]==CINF)continue;
				for(int i=0;i<g[v].size();i++){
					const auto &e=g[v][i];
					if(flow[v][i]>0 && dist[e.dst]>dist[v]+e.weight.second){
						dist[e.dst]=dist[v]+e.weight.second;
						prevv[e.dst]=v;preve[e.dst]=i;
						update=true;
					}
				}
			}
		}
		if(dist[t]==CINF){
			ret.feasible = false;
			return ret;
		}
		Flow d=f;
		for(int v=t;v!=s;v=prevv[v]){
			d=min(d,flow[prevv[v]][preve[v]]);
		}
		f-=d;
		res+=d*dist[t];
		for(int v=t;v!=s;v=prevv[v]){
			const auto &e=g[prevv[v]][preve[v]];
			flow[prevv[v]][preve[v]]-=d;
			flow[v][e.rev]+=d;
		}
	}
	ret.feasible = true;
	ret.val = res;
	return ret;
}
