const int SMAX=10000;
int pdiv[SMAX];
vector<int> primes;
//{{{ eratosthenes
void sieve_of_eratosthenes(int n) {
	for (int i = 2; i < n; ++i)
		pdiv[i] = i;
	for (int i = 2; i*i < n; ++i)
		if (pdiv[i]==i)
			for (int j = i*i; j < n; j+=i)
				pdiv[j] = i;
}
//}}}
bool is_prime(int n){
	if(n==1)return false;
	for(int i=0;;i++){
		int p = primes[i];
		if(p*p>n)break;
		if(n%p==0)return false;
	}
	return true;
}

void solve(long long N, vector<long long> x){
	vector<int> v0,v1;
	for(int i=0;i<x.size();){
		int j;
		for(j=i+1;j<x.size();j++){
			if(x[j-1]+1!=x[j])break;
		}
		//[ x[i], x[j-1]+1 )
		vector<Int> v{x[i],x[j-1]+1};
		for(auto u:v){
			if(u%2==0)v0.push_back(u);
			else v1.push_back(u);
		}
		i = j;
	}
	Graph<pair<int,Int> > g(v0.size()+v1.size()+2);
	int s = v0.size() + v1.size(), t = s + 1;
	REP(i,v0.size())REP(j,v1.size()){
		if(is_prime(abs(v0[i]-v1[j]))){
			addFlowEdge(g,{i,j+v0.size()},{10000,1});
		}else{
			addFlowEdge(g,{i,j+v0.size()},{10000,3});
		}
	}
	REP(i,v0.size())addFlowEdge(g,{s,i},{1,0});
	REP(j,v1.size())addFlowEdge(g,{j+v0.size(),t},{1,0});
	Int ans = INF;
	int r = v0.size()%2;
	for(int f=r;;f+=2){
		if(v0.size()<f or v1.size()<f)break;
		auto mcf(minCostFlow(g,{s,t},f));
		assert(mcf);
		auto c = *mcf;
		c+=v0.size()+v1.size()-f*2;
		ans = min(c,ans);
	}
	fout<<ans<<endl;
}

int main(){
	Graph<> g;
	sieve_of_eratosthenes(SMAX);
	for(int p=2;p<SMAX;p++)if(pdiv[p]==p)primes.push_back(p);
	long long N;
	fin >> N;
	vector<long long> x(N-1+1);
	for(int i = 0 ; i <= N-1 ; i++){
		fin >> x[i];
	}
	solve(N, x);
	return 0;
}

